import React from "react" ;
import {Link} from "react-router-dom";
import axios from "axios";
// import RichText from 'prismic-reactjs';
import {
  Container ,
  Card ,
  ListGroup ,
  Button,
  Form ,
  Col ,
  Popover ,
  OverlayTrigger ,
  Badge ,
  Alert ,
  Row ,

} from 'react-bootstrap';
import { connect } from "react-redux";

import RichText from './Misc/RichText';
import LoaderSpinner from './Misc/LoaderSpinner'
import {
  all_articles_url,

} from "../backend-urls.js" ;

import "../content/css/App.css";




class Home extends React.Component {

  state = {
    error: null , //stores the error that comes from the backend
    loading : true , //to determine if the page is loading
    articles : [] , //stores all of the articles retrieved from the database
    pinned_article : [] , //stores the data for the pinned article

  }

  componentDidMount(){
    this.setState({
      loading: true ,

    })
    this.handleGetArticles();
    this.handleGetPinnedArticle(this.state.articles) ;
  }

  //retrieves all the articles from the database
  handleGetArticles = () => {
    axios
      .get(all_articles_url)
      .then(response => {
        var pinned_article = []

        for(var article in response.data){
          if (article.pinned === true){
            console.log(article.id)
            pinned_article.id = article.id ;
            pinned_article.title = article.title ;
            pinned_article.author = article.author ;
            pinned_article.created_date = article.created_date ;
            pinned_article.body = article.body ;
            this.setState({
              pinned_article : pinned_article
            })
          }
        }
        this.setState({
          loading: false ,
          articles: response.data ,
        })
      })
      .catch(error => {
        this.setState({
          error: error ,
          loading : false
        })
      })
  }

  //selects the article where pinned = True
  handleGetPinnedArticle = (articles) => {
    var pinned_article = []
    for(var article in articles){
      if (article.pinned === true){
        console.log(article.id)
        pinned_article.id = article.id ;
        pinned_article.title = article.title ;
        pinned_article.author = article.author ;
        pinned_article.created_date = article.created_date ;
        pinned_article.body = article.body ;
        this.setState({
          pinned_article : pinned_article
        })
      }
    }

  }



  render(){

    const {
      loading ,
      error ,
      articles ,
      pinned_article ,

    } = this.state

    const {
      authenticated ,
      token ,

    } = this.props


    console.log(pinned_article.body) ;

    //for email popover
    const email_popover = (
      <Popover id="popover-basic">
        <Popover.Title as="h3">Email Updates</Popover.Title>
        <Popover.Content>
          <Form >
            <Form.Row>
              <Col>
                <Form.Control type="email" placeholder="Enter email" />
              </Col>
              <Col>
                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </Col>
            </Form.Row>
          </Form>
        </Popover.Content>
      </Popover>
    )

    return(

      <React.Fragment>
        <Container>
          <Row>
            <Col sm={{span:4}}>
              <h1 className="verdana-font">
                The Apollo Blog
              </h1>
            </Col>
            <Col sm={{offset:9}}>
              {
                authenticated ?
                  token === "8582b323219d80196bb1e017411808a67fe70365" &&
                  <div className="float-right">
                    <Link to="/create-article" >
                      <Button variant="outline-secondary">Write an Article</Button>
                    </Link>
                  </div>
                :
                  <div className="float-right">
                    <OverlayTrigger trigger="click" placement="left" overlay={email_popover}>
                      <Button variant="outline-primary">Sign up for email updates</Button>
                    </OverlayTrigger>
                  </div>
              }


            </Col>
          </Row>


        </Container>
        <hr />
        <br />

        <ListGroup variant="flush">


          <React.Fragment>
              <ListGroup.Item>
                <Link to={'blog/article/'+pinned_article.id} className="article-link">
                  <Card className="width-100-percent" id={pinned_article.id}>
                    <Card.Body>
                      <Card.Title>{pinned_article.title}</Card.Title>
                      <Card.Subtitle className="mb-2 text-muted">By <a href='#'>{pinned_article.author}</a> <Badge>{  new Date(pinned_article.created_date).toDateString() } </Badge></Card.Subtitle>
                      <Card.Text >
                        <RichText text={pinned_article.body.substring(0, 200) + '...'} />
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Link>
              </ListGroup.Item>
          </React.Fragment>


          {
            loading === true ?
              <LoaderSpinner />
            :
              articles.length > 0 ?

                  //reverse() makes the articles appear in reverse chronological order
                    articles.reverse().map(article =>  (
                        <ListGroup.Item>
                          <Link to={'blog/article/'+article.id} className="article-link">
                            <Card className="width-100-percent" id={article.id}>
                              <Card.Body>
                                <Card.Title>{article.title}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">By <a href='#'>{article.author}</a> <Badge>{  new Date(article.created_date).toDateString() } </Badge></Card.Subtitle>
                                <Card.Text >
                                  <RichText text={article.body.substring(0, 200) + '...'} />
                                </Card.Text>
                              </Card.Body>
                            </Card>
                          </Link>
                        </ListGroup.Item>
                      )
                    )



              :
                <Alert variant="dark">
                  There was an error retrieving our articles
                </Alert>


          }

        </ListGroup>


      </ React.Fragment>


    )
  }
}


const mapStateToProps = (state) => {
  return {
    token : state.auth.token ,
    authenticated: state.auth.token !== null ,

  }
}


export default
  connect(
    mapStateToProps,

  )(Home) ;
