import React from "react";
import {
  Button ,
  FormGroup ,
  FormControl ,
  Badge ,
  Alert ,

} from "react-bootstrap";
import { connect } from "react-redux";
import { NavLink, Redirect } from "react-router-dom";
import {withRouter} from 'react-router';


import "../../content/css/App.css";
import { authLogin } from "../../store/actions/auth";



class ResetPassword extends React.Component {

  state = {
    loading : false , //stores the loading page
    error : null , //stores any error that would get returned from the backend
    email : "" , //stores the email from the input
  }

  handleEmail = (e) => {
    this.setState({email : e.target.value}) ;
  }

  handleValidated = () => {
    return !(this.state.email.length > 1  )
  }


  render(){
    const {
      loading ,
      error ,
      email ,
    } = this.state ;

    return(
      <div>
      <div className="account-form">
        <h3>Reset Password </h3>
        <hr />
        <FormGroup controlId="email" bsSize="large">
          Email
          <FormControl
            required
            autoFocus
            value={email}
            onChange= {this.handleEmail}
          />
        </FormGroup>

        <Button
          block
          bsSize="large"
          disabled={
            (this.handleValidated())
          }
          type="submit"
        >
          Login
        </Button>

      </div>

      <div className="account-form">

        // <form onSubmit={this.handleSubmit}>
          <h3>Login </h3>
          <hr />
          <FormGroup controlId="email" bsSize="large">
            Username
            <FormControl
              required
              autoFocus
              onChange= {this.handleUsername}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            Password
            <FormControl
              required
              onChange= {this.handlePassword}
              type="password"
            />
          </FormGroup>
          <Button
            block
            bsSize="large"
            disabled={
              !(this.handleValidated())
            }
            type="submit"
          >
            Login
          </Button>
          <Badge> New to Apollo? <NavLink to="/signup">Sign Up</NavLink> </Badge>
          <br/>
          <Badge> <NavLink to="/reset-password">Forgot your password? </NavLink> </Badge>
            <br />
            {
              error &&
                <Alert variant="danger">
                  {
                    //loops through all the returned errors and prints them with a bullet
                    // Object.keys(errors).map(e => {
                    //   return <li>{errors[e][0]}</li>
                    // })
                  }
                </Alert>

            }
        // </form>
      </div>
      </div>
    )
  }



}

export default ResetPassword;
