import React from "react";
import {
  Button ,
  FormGroup ,
  FormControl ,
  Badge ,
  Alert ,

} from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";

import "../../content/css/App.css";
import { authSignup } from "../../store/actions/auth";

class  Signup extends React.Component  {
  state = {
    loading: null , //for whne the page is loading
    error: null , //holds any error with submitting the form
    email : "" , //holds the email value
    username : "" , //holds the email value
    password : "" , //holds the password value
    confirm_password : "" , //holds the confirm_password value
    passwords_match : true ,

  }

  handleEmail = (e) => {
    this.setState({
      email : e.target.value
    })
  }

  handleUsername = (e) => {
    this.setState({
      username : e.target.value
    })
  }

  handlePassword = (e) => {
    if(this.state.confirm_password !== e.target.value){
      this.setState({
        passwords_match : false ,
        password : e.target.value
      })
    }else{
      this.setState({
        passwords_match : true ,
        password : e.target.value
      })
    }
  }

  handleConfirmPassword = (e) => {
    if(this.state.password !== e.target.value){
      this.setState({
        passwords_match : false ,
        confirm_password : e.target.value
      })
    }else{
      this.setState({
        passwords_match : true ,
        confirm_password : e.target.value
      })
    }
  }


  handleValidated = () => {
    var email_valid = this.state.email.length > 0
    var username_valid = this.state.username.length > 0
    var password_valid = this.state.password.length > 8
    var confirm_password_valid = this.state.confirm_password.length > 8
    var passwords_match = this.state.password_valid === this.state.confirm_password_valid
    return email_valid && username_valid && password_valid && confirm_password_valid && passwords_match && (this.state.password === this.state.confirm_password)  ;
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const {email, username, password , confirm_password} = this.state;
    console.log(email) ;
    this.props.signup(username, email, password , confirm_password)
  }

  render(){
    const {
      email ,
      username ,
      password ,
      confirm_password ,
      passwords_match ,

    } = this.state ;

    const {
      error
    } = this.state ;

    console.log(passwords_match)

    return (
      <div className="account-form">

        <form onSubmit={this.handleSubmit}>
          <h3>Signup </h3>
          <hr />
          <FormGroup controlId="email" bsSize="large">
            Email
            <FormControl
              required
              autoFocus
              type="email"
              value={email}
              onChange={this.handleEmail}
            />
          </FormGroup>
          <FormGroup controlId="username" bsSize="large">
            Username
            <FormControl
              required
              value={username}
              onChange={this.handleUsername}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            Password
            <FormControl
              required
              value={password}
              onChange={this.handlePassword}
              type="password"
            />
          </FormGroup>
          <FormGroup controlId="confirm_password" bsSize="large">
            Confirm Password
            <FormControl
              required
              value={confirm_password}
              onChange={this.handleConfirmPassword}
              type="password"
            />
          </FormGroup>

          <Button block bsSize="large" disabled={!this.handleValidated()} type="submit">
            Signup
          </Button>
          <Badge> Already have an account? <NavLink to="/login">Login </NavLink> </Badge>

          {
            !passwords_match &&
            <Alert variant="danger">Your passwords must match</Alert>
          }

          {
            error &&
            <Alert variant="danger">error</Alert>
          }


        </form>
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    token: state.auth.token,
  };
};


const mapDispatchToProps = dispatch => {
  return {
    signup: (email, username, password1 , password2) => dispatch(authSignup(email, username, password1 , password2)),

  };
};

export default
  connect(
    mapStateToProps ,
    mapDispatchToProps
  )(Signup) ;
