export const endpoint = `http://127.0.0.1:8000/api/`;


export const all_articles_url =     endpoint+`all-articles/` ;
// export const article_url = (id) =>  endpoint+`article/${id}`
export const article_url = (id) =>  endpoint+`article/`+id+`/` ;

export const create_article_url = endpoint+`article/create/` ;
