import React from "react" ;
import {Link} from "react-router-dom";
import axios from "axios";

import {
  Container ,
  Card ,
  ListGroup ,
  Button,
  Form ,
  Col ,
  Popover ,
  OverlayTrigger ,
  Badge ,
  Alert ,
  Row ,

} from 'react-bootstrap';
import { connect } from "react-redux";
// found at https://www.npmjs.com/package/react-bootstrap-icons
import { Pencil , StarFill , CheckCircle } from 'react-bootstrap-icons';

import RichText from './Misc/RichText';
import LoaderSpinner from './Misc/LoaderSpinner'
import {
  all_articles_url ,
  create_email_url ,
} from "../backend-urls.js" ;

import "../content/css/App.css";
import RegisterEmail from './Misc/RegisterEmail';



class Home extends React.Component {

  state = {
    error: null , //stores the error that comes from the backend
    loading : true , //to determine if the page is loading
    articles : [] , //stores all of the articles retrieved from the database
    pinned_article : null , //stores the data for the pinned article
    // email: "" , //stores the email from the "add email" widget
    // email_submitted : null , //stores the response value after a user submits their email

  }

  componentDidMount(){
    this.setState({
      loading: true ,

    })
    this.handleGetArticles();
  }

  //retrieves all the articles from the database
  handleGetArticles = () => {
    axios
      .get(all_articles_url)
      .then(response => {

        //gets all the information for the article with pinned = true. had to put it in here because this function wouldn't set the state fast enough for another method to access it in the componentDidMount.
        var articles = response.data
        var pinned_article = []
        for(var article_id in articles){
          if (response.data[article_id].pinned ){
            pinned_article.id = articles[article_id].id ;
            pinned_article.title = articles[article_id].title ;
            pinned_article.author = articles[article_id].author ;
            pinned_article.created_date = articles[article_id].created_date ;
            pinned_article.body = articles[article_id].body.substring(0, 500) ;
            break ;
          }
        }

        this.setState({
          loading: false ,
          articles: response.data ,
          pinned_article : pinned_article ,

        })
      })
      .catch(error => {
        this.setState({
          error: error ,
          loading : false
        })
      })
  }


  // handlePostEmail = () => {
  //   var data = new FormData();
  //   data.append('email' , this.state.email) ;
  //
  //   axios
  //     .post(create_email_url , data)
  //     .then(response => {
  //       this.setState({
  //         email_submitted : true ,
  //       })
  //     })
  //     .catch(error => {
  //       this.setState({
  //         error: error.response.data  ,
  //       })
  //     })
  // }

  // handleEmailValidated = () => {
  //   var email = this.state.email
  //   //these are the most abstract validations that I can think of.. length is greater than 5 (contains an @, a ., and a letter before/after each. ie "a@b.co" would pass (I'm not sure if there are top-level domains with single letter so i just required 2 for the tld))
  //   var email_length_more_than_four = email.length > 5
  //   var email_contains_at = email.includes("@")
  //   var email_contains_period = email.includes(".")
  //
  //   return !(email_length_more_than_four && email_contains_at && email_contains_period )
  // }
  //
  // handleEmail = (text) => {
  //   this.setState({
  //     email: text.target.value
  //   });
  // }





  render(){

    const {
      loading ,
      error ,
      articles ,
      pinned_article ,
      email_submitted ,

    } = this.state

    const {
      authenticated ,
      token ,

    } = this.props


    //for email popover
    const email_popover = (
      <Popover id="popover-basic" className="max-width-800px">
        <Popover.Title as="h3">Email Updates</Popover.Title>
        <Popover.Content >
            <Row >
              <Col>
                <Form.Control
                  // style={{width:'200px'}}
                  className="width-250px"
                  type="email"
                  placeholder="Enter email"
                  onChange={this.handleEmail}
                />
                {
                  error&&
                    <small style={{color:'red'}} >{error}</small>
                  }
              </Col>
              <Col>
                {
                  email_submitted ?
                    <CheckCircle className="text-success font-size-2-5em" />
                  :
                    <Button
                      disabled={this.handleEmailValidated()}
                      variant="primary"
                      type="submit"
                      onClick={this.handlePostEmail}
                      className="float-right"
                    >
                      Submit
                    </Button>
                }
              </Col>
            </Row>
        </Popover.Content>
      </Popover>
    )


    return(

      <React.Fragment>
        <Container>
          <Row>
            <Col sm={{span:4}}>
              <h1 className="verdana-font">
                The Apollo Blog
              </h1>
            </Col>
            <Col sm={{offset:9}}>
              {
                authenticated ?
                  token === "8582b323219d80196bb1e017411808a67fe70365" &&
                    <div className="float-right">
                      <Link to="/create-article" >
                        <Button variant="outline-secondary">Write an Article <Pencil /></Button>
                      </Link>
                    </div>
                :
                  <div className="float-right">

                    <RegisterEmail text="Sign up for email updates"/>
                  </div>

              }


            </Col>
          </Row>


        </Container>
        <hr />
        <br />

        <ListGroup variant="flush">
          {
            pinned_article !== null &&
              <ListGroup.Item >
                 <Link to={'blog/article/'+pinned_article.id} className="article-link">
                   <Card className="width-100-percent margin-bottom-75px" id={pinned_article.id}>
                     <Card.Body>
                       <Card.Title>{pinned_article.title} <StarFill color="royalblue" className="float-right font-size-1-25em" /></Card.Title>
                       <Card.Subtitle className="mb-2 text-muted">By <a href='#'>{pinned_article.author}</a> <Badge>{  new Date(pinned_article.created_date).toDateString() } </Badge></Card.Subtitle>
                       <Card.Text >
                          <RichText text={pinned_article.body +"..." } />
                       </Card.Text>
                     </Card.Body>
                   </Card>
                 </Link>
                </ListGroup.Item>
          }

          {
            loading === true ?
              <LoaderSpinner />
            :
              articles.length > 0 ?
                //reverse() makes the articles appear in reverse chronological order
                articles.map(article =>  (
                    <ListGroup.Item>
                      <Link to={'blog/article/'+article.id} className="article-link">
                        <Card className="width-100-percent" id={article.id}>
                          <Card.Body>
                            <Card.Title>{article.title}</Card.Title>
                            <Card.Subtitle className="mb-2 text-muted">By <a href='#'>{article.author}</a> <Badge>{  new Date(article.created_date).toDateString() } </Badge></Card.Subtitle>
                            <Card.Text >
                              <RichText text={article.body.substring(0, 200) + '...'} />
                            </Card.Text>
                          </Card.Body>
                        </Card>
                      </Link>
                    </ListGroup.Item>
                  )
                )
              :
                <Alert variant="dark">
                  There was an error retrieving our articles
                </Alert>


          }

        </ListGroup>


      </ React.Fragment>


    )
  }
}


const mapStateToProps = (state) => {
  return {
    token : state.auth.token ,
    authenticated: state.auth.token !== null ,

  }
}

const mapDispatchToProps = (state) => {
  return {
  }
}


export default
  connect(
    mapStateToProps,

  )(Home) ;
