import React, { useState } from "react";
import {
  Button ,
  FormGroup ,
  FormControl ,
  Badge ,

} from "react-bootstrap";
import { connect } from "react-redux";
import { NavLink, Redirect } from "react-router-dom";


import "../../content/css/App.css";
import { authLogin } from "../store/actions/auth";




class Login extends React.Component {
  state = {
    email: null , //stores the email that is getting entered
    password: null , //stores the password that is getting entered

  }
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  validateForm = (email, password) =>  {
    return email.length > 0 && password.length > 8;
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const {email, password} = this.state;
    this.props.login(email, password)
  }

  render(){
    const { error, loading, token } = this.props;
    const { email, password } = this.state;

    //redirects the user to the home page if they are logged in
    // token &&
    //   <Redirect to='/' />

    return (
      <div className="account-form">
        {error && <p>{this.props.error.message}</p>}

        <form onSubmit={this.handleSubmit(username , password)}>
          <h3>Login </h3>
          <hr />
          <FormGroup controlId="email" bsSize="large">
            Email
            <FormControl
              autoFocus
              type="email"
              value={email}
              onChange={e => handleChange(e.target.value)}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            Password
            <FormControl
              value={password}
              onChange={e => handleChange(e.target.value)}
              type="password"
            />
          </FormGroup>
          <Button block bsSize="large" disabled={!validateForm(username, password)} type="submit">
            Login
          </Button>
          <Badge>   New to Apollo? <NavLink to="/signup">Sign Up</NavLink> </Badge>
        </form>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    token: state.auth.token,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    login: (username, password) => dispatch(authLogin(username, password)),
    // mergeCartOnLogin: () => dispatch(mergeCartOnLogin()),

  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login) ;
