import React from "react";
import {Link} from "react-router-dom";
import axios from 'axios';
import {
  Container ,
  Badge,
  Col,
  Row,
  Form,
  ListGroup,
  Button,
  OverlayTrigger ,
  Popover,
  Card,

} from 'react-bootstrap';
import "../content/css/App.css";

import {
  create_article_url ,

} from "../backend-urls.js" ;

//for react-medium-editor. found at https://github.com/wangzuo/react-medium-editor
import Editor from 'react-medium-editor';
require('medium-editor/dist/css/medium-editor.css');
require('medium-editor/dist/css/themes/default.css');


class CreateArticle extends React.Component  {

  state = {
    loading : false ,
    author : null ,
    title : null ,
    body : null,

  }

  componentDidMount(){
    this.setState({
      loading : false
    })
  }

  handleAuthor = (text) => {
    this.setState({
      author: text
    });
  }

  handleTitle = (text) => {
    this.setState({
      title: text
    });
  }

  handleBody = (text) => {
    this.setState({
      body: text
    });
  }

  submitForm = () => {

    var data = new FormData(); // creates a new FormData object

    axios
      .post(create_article_url , {
        author : this.state.author,
        title : this.state.title ,
        body : this.state.body ,
      })
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.log(error)
        this.setState({
          error: error ,
          loading : false
        })
      }) ;
  }


  render(){
    const {
      author ,
      title ,
      body ,
    } = this.state ;


    return(

      <Container>
        <Row>
            <Col xs={2}>
            </Col>
            <Col xs={8} >
            {console.log(body)}
              <Card >
                <Card.Header><h3>Add an Article</h3></Card.Header>
                <Card.Body>
                  <Card.Text>
                    <Form>
                      <Form.Row>
                        <Form.Label>Your Name:</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Enter Name"
                          onChange = {this.handleAuthor}
                        />
                      </Form.Row>
                      <br />
                      <Form.Row>
                        <Form.Label>Article Title:</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Enter Name"
                          onChange = {this.handleTitle}
                        />
                      </Form.Row>
                      <br/>
                      <Form.Row>
                        <Col>
                          <Form.Label>Article Body:</Form.Label>
                          <Editor
                            className="editor_form_control"
                            text={this.state.text}
                            onChange={this.handleBody}
                            options={{
                                placeholder: {
                                  text: 'Start Writing...',
                                  hideOnClick: false
                                } ,
                              }}
                            />
                            <small>Hint: highlight text that you want to change</small>
                        </Col>
                      </Form.Row>
                    </Form>
                  </Card.Text>
                  <Button
                    variant="primary"
                    onClick={this.submitForm}
                  >
                    Add Article
                  </Button>
                </Card.Body>
              </Card>
            </Col>
        </Row>
      </Container>
    )
  }

}

  export default CreateArticle;
