import React from 'react';
import axios from "axios";


import {
  Container ,
  Card ,
  ListGroup ,
  Button,
  Col ,
  Badge ,
  Alert ,
  Row ,

} from 'react-bootstrap';
import { connect } from "react-redux";


class Profile extends React.Component {
  state = {
    error: null //holds the error

  }

  render(){
    const {
      username
    } = this.props

    return(
      <React.Fragment>
        <h3>profile page lol </h3>
        <h4>{username}</h4>
        <Card style={{ width: '18rem' }}>
          <ListGroup variant="flush">
            <ListGroup.Item>Account Info</ListGroup.Item>
            <ListGroup.Item>Email Preferences</ListGroup.Item>
          </ListGroup>
        </Card>
      </React.Fragment>
    )
  }

}



const mapStateToProps = (state) => {
  return {
    username : state.auth.username ,
  }
}

export default
  connect(
    mapStateToProps ,

  )(Profile)
