import axios from "axios";
import * as actionTypes from "./actionTypes";
// import {
//   mergeCartOnLogin,
//   removeCartOnLogout,
// } from "./cart";


export const authStart = () => {
  return {
    type: actionTypes.AUTH_START
  };
};

export const authSuccess = (token , username) => {
  return {
    type: actionTypes.AUTH_SUCCESS,
    token: token,
    username : username,
    staff : staff
  };
};

export const authFail = error => {
  return {
    type: actionTypes.AUTH_FAIL,
    error: error
  };
};

export const logout = () => {
  return {
    type: actionTypes.AUTH_LOGOUT
  };
};


export const checkAuthTimeout = (expirationTime) => {
  return dispatch => {
    setTimeout(() => {
      dispatch(logout());
    }, expirationTime * 1000);
  };
};


export const authLogin = (username, password) => {
  return dispatch => {
    dispatch(authStart());
    axios
      .post("http://127.0.0.1:8000/api/login/", {
        username: username,
        password: password
      })
      .then(res => {
        const token = res.data.key;
        const staff = res.data.staff;
        const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
        localStorage.setItem("staff", username);
        localStorage.setItem("username", username);
        localStorage.setItem("token", token);
        localStorage.setItem("expirationDate", expirationDate);
        dispatch(authSuccess(token , username , staff));
        dispatch(checkAuthTimeout(10000));

        //merges the cart when the user logs in. mergeCartOnLogin is located in actions/cart.js
        // dispatch( mergeCartOnLogin(token) )

      })
      .catch(err => {
        // console.log(err.response.data.non_field_errors)
        dispatch(authFail(err));
      });
  };
};


export const authSignup = (username, email, password1, password2) => {
  return dispatch => {
    dispatch(authStart());
    axios
      .post("http://127.0.0.1:8000/rest-auth/registration/", {
        username: username,
        email: email,
        password1: password1,
        password2: password2
      })
      .then(res => {
        const token = res.data.key;
        const expirationDate = new Date(new Date().getTime() + 86400 * 1000);
        localStorage.setItem("token", token);
        localStorage.setItem("expirationDate", expirationDate);
        dispatch(authSuccess(token));
        dispatch(checkAuthTimeout(3600));
      })
      .catch(err => {
        dispatch(authFail(err));
      });
  };
};

//added username to the authSuccess part and in the parameters of the function on 11/5/19
export const authCheckState = (username) => {
  return dispatch => {
    const token = localStorage.getItem("token");
    const username = localStorage.getItem("username");
    if (token === undefined) {
      dispatch(logout());
    } else {
      const expirationDate = new Date(localStorage.getItem("expirationDate"));
      if (expirationDate <= new Date()) {
        dispatch(logout());
      } else {
        dispatch(authSuccess(token , username ));
        dispatch(
          checkAuthTimeout(
            (expirationDate.getTime() - new Date().getTime()) / 1000
          )
        );
      }
    }
  };
};




export const logoutUser = () => {
    return dispatch => {
      //removes the user token and expiration date from local storage
      localStorage.removeItem("token");
      localStorage.removeItem("expirationDate");
      dispatch( logout() )
    }
}




