import React from "react" ;
import {withRouter} from "react-router";
import {Link} from "react-router-dom";
import axios from "axios";
import {
  Container ,
  Spinner,
  Card ,
  ListGroup ,
  Button,
  Form ,
  Col ,
  Popover,
  OverlayTrigger ,

} from 'react-bootstrap';

import {
  all_articles_url,
} from "../backend-urls.js" ;
import "../content/css/App.css";


class Home extends React.Component {

  state = {
    loading: false ,
    error: null ,
    articles : [] ,

  }

  componentDidMount(){
    this.setState({
      loading:true ,
    })
    this.handleGetArticles();
  }


  handleGetArticles = () => {
    axios
      .get(all_articles_url)
      .then(response => {
        this.setState({
          loading: false ,
          articles: response ,
        })
      })
      .catch(error => {
        this.setState({
          error: error ,
          loading : false
        })
      })
  }



  render(){
    const{
      loading,
      error,
      articles,
    } = this.state

    //for email popover
    const email_popover = (
      <Popover id="popover-basic">
        <Popover.Title as="h3">Email Updates</Popover.Title>
        <Popover.Content>
          <Form >
            <Form.Row>
              <Col>
                <Form.Control type="email" placeholder="Enter email" />
              </Col>
              <Col>
                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </Col>
            </Form.Row>
          </Form>
        </Popover.Content>
      </Popover>
    )

    return(



      <React.Fragment>

        <Container>
          <Link to="/create-article" >
            <Button variant="outline-secondary">Write an Article</Button>
          </Link>

          <OverlayTrigger trigger="click" placement="right" overlay={email_popover}>
            <Button variant="outline-primary">Sign up for email updates</Button>
          </OverlayTrigger>
        </Container>

        <br />

        <ListGroup variant="flush">
          <ListGroup.Item>

            <Link to="blog/article/1" className="article-link">
              <Card className="width-100-percent" >
                <Card.Body>
                  <Card.Title>Our Mission</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">By <a href='#'>Connor Davidson</a></Card.Subtitle>
                  <Card.Text>
                    Some quick example text to build on the card title and make up the bulk of
                    the card's content.
                  </Card.Text>
                </Card.Body>
              </Card>
            </Link>

          </ListGroup.Item>

          <ListGroup.Item>
            <Card className="width-100-percent" >
              <Card.Body>
                <Card.Title>Our Mission</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">By <a href='#'>Connor Davidson</a></Card.Subtitle>
                <Card.Text>
                  Some quick example text to build on the card title and make up the bulk of
                  the card's content.
                </Card.Text>
              </Card.Body>
            </Card>
          </ListGroup.Item>

          <ListGroup.Item>
            <Card className="width-100-percent" >
              <Card.Body>
                <Card.Title>Our Mission</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">By <a href='#'>Connor Davidson</a></Card.Subtitle>
                <Card.Text>
                  Some quick example text to build on the card title and make up the bulk of
                  the card's content.
                </Card.Text>
              </Card.Body>
            </Card>
          </ListGroup.Item>
        </ListGroup>


      </ React.Fragment>


    )
  }
}



export default Home ;
