import React from "react";
import {Link} from "react-router-dom";
import axios from 'axios';
import {
  Container ,
  Badge ,
  Col ,
  Row ,
  Form ,
  ListGroup,
  Button ,
  OverlayTrigger ,
  Popover ,
  Card ,
  Alert ,

} from 'react-bootstrap';
import "../content/css/App.css";

import {
  create_article_url ,

} from "../backend-urls.js" ;

//for react-medium-editor. found at https://github.com/wangzuo/react-medium-editor
import Editor from 'react-medium-editor';
require('medium-editor/dist/css/medium-editor.css');
require('medium-editor/dist/css/themes/default.css');


class CreateArticle extends React.Component  {

  state = {
    error: null , //stores the error that comes from the backend
    loading : false , //to determine if the page is loading
    author : null , //stores the author (from the author input)
    title : null , //stores the title (from the title input)
    body : null , //stores the body (from the body input)
    article_submitted : false , //to determine if the article was successfully submitted (if true, display a banner at the bottom of the page and redirect the user)
    countdown: 5 , //countdown to redirect, after the article gets submitted

  }

  componentDidMount(){
    this.setState({
      loading : false
    })
  }

  handleAuthor = (text) => {
        console.log(text.target.value)
    this.setState({
      author: text.target.value
    });
  }

  handleTitle = (text) => {
    console.log(text)
    this.setState({
      title: text.target.value
    });
  }

  handleBody = (text) => {
    console.log(text)
    this.setState({
      body: text
    });
  }

  submitForm = () => {

    // creates a new FormData object and adds all the form data to it
    var data = new FormData();
    data.append('author' , this.state.author) ;
    data.append('title' , this.state.title) ;
    data.append('body' , this.state.body) ;

    axios
      .post(create_article_url , data)
      .then(response => {
        this.setState({
          article_submitted : true
        })
        console.log(response.data);
      })
      .catch(error => {
        console.log(error)
        this.setState({
          error: error ,
          loading : false
        })
      }) ;
  }

  countdown = ( s ) => {
    setInterval(function(){
      s -= 1 ;
      // this.setState({
      //   countdown : s
      // })

    } , 1000 );
  }



  // setInterval(function(){
  //
  //  alert(this.state.countdown); }, 1000)


  render(){
    const {
      author ,
      title ,
      body ,
      article_submitted ,
      countdown ,

    } = this.state ;



    // if(article_submitted === true){
    //   this.countdown(5).bind(this)
    // }

    article_submitted &&
      setInterval(function(){ alert("Popup window!"); }, 500);


    return(

      <Container>
        <Row>
            <Col xs={2}>
            </Col>
            <Col xs={8} >
            {console.log(body)}
              <Card >
                <Card.Header><h3>Add an Article</h3></Card.Header>
                <Card.Body>
                  <Card.Text>
                    <Form>
                      <Form.Row>
                        <Form.Label>Your Name:</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Enter Name"
                          onChange = {this.handleAuthor}
                        />
                      </Form.Row>
                      <br />
                      <Form.Row>
                        <Form.Label>Article Title:</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Enter Name"
                          onChange = {this.handleTitle}
                        />
                      </Form.Row>
                      <br/>
                      <Form.Row>
                        <Col>
                          <Form.Label>Article Body:</Form.Label>
                          <Editor
                            className="editor_form_control"
                            text={this.state.text}
                            onChange={this.handleBody}
                            options={{
                                placeholder: {
                                  text: 'Start Writing...',
                                  hideOnClick: false
                                } ,
                              }}
                            />
                            <small>Hint: highlight text that you want to change</small>
                        </Col>
                      </Form.Row>
                    </Form>
                  </Card.Text>
                  <Button
                    variant="primary"
                    onClick={this.submitForm}
                  >
                    Add Article
                  </Button>
                </Card.Body>
              </Card>
              {


                article_submitted  &&

                  <React.Fragment>
                    <br/>

                    <meta http-equiv="refresh" content="5;url=/" />
                    <Alert variant="success">
                      You successfully created an article! You will be redirected in {countdown} seconds
                    </Alert>

                  </React.Fragment>

              }


            </Col>
        </Row>
      </Container>
    )
  }

}

  export default CreateArticle;
