import React from "react";
import {Link} from "react-router-dom";
import {
  Container ,
  Badge,
  Col,
  Row,
  Form,
  ListGroup,
  Button,
  OverlayTrigger ,
  Popover,
  Card,

} from 'react-bootstrap';
import "../content/css/App.css";


//for react-medium-editor. found at https://github.com/wangzuo/react-medium-editor
import Editor from 'react-medium-editor';
require('medium-editor/dist/css/medium-editor.css');
require('medium-editor/dist/css/themes/default.css');


class CreateArticle extends React.Component  {

  state = {
    article_id : null,

  }

  componentDidMount(){
    this.setState({
      article_id : this.props.match.params.article_id
    })
  }


  render(){
    return(

      <Card >
        <Card.Header><h3>Add an Article</h3></Card.Header>
        <Card.Body>
          <Card.Text>
            <Form>
              <Form.Row>
                <Form.Label>Name:</Form.Label>
                <Form.Control type="text" placeholder="Enter Name" />
              </Form.Row>
              <br />
              <Form.Row>
                <Form.Label>Title:</Form.Label>
                <Form.Control type="text" placeholder="Enter Name" />
              </Form.Row>
              <br/>
              <Form.Row>
                <Col>
                  <Form.Label>Body:</Form.Label>
                  <Editor
                    style={{border:"1px solid lightgre " , height:"500px"}}
                    text={this.state.text}
                    onChange={this.handleChange}
                    options={{
                        placeholder: {
                          text: 'Enter Comment',
                          hideOnClick: true
                        } ,
                      }}
                    />
                    <small>Hint: highlight text that you want to change</small>
                </Col>
              </Form.Row>
            </Form>
          </Card.Text>
          <Button variant="primary">Add Comment</Button>
        </Card.Body>
      </Card>
    )
  }

}

  export default CreateArticle;
