import React from "react";
import axios from "axios";
import {
  Button ,
  FormGroup ,
  FormControl ,
  Badge ,
  Alert ,

} from "react-bootstrap";
import LoaderSpinner from '../Misc/LoaderSpinner'

import "../../content/css/App.css";
import {
  reset_password_url ,

} from "../../backend-urls.js" ;


class SetNewPassword extends React.Component {
  state = {
    loading : false , //determines if the page is laoding
    error : null , //holds the error that gets returned from the database
    password : "" , //holds the password value
    confirm_password : "" , //holds the confirm_password value

  }


  render(){
    return(

      <div className="account-form">
        <h5>set new password here </h5>


          <FormGroup controlId="password" bsSize="large">
            Password
            <FormControl
              required
              value={password}
              onChange={this.handlePassword}
              type="password"
            />
          </FormGroup>
          <FormGroup controlId="confirm_password" bsSize="large">
            Confirm Password
            <FormControl
              required
              value={confirm_password}
              onChange={this.handleConfirmPassword}
              type="password"
            />
          </FormGroup>

          <Button block bsSize="large" disabled={!this.handleValidated()} type="submit">
            Change Password
          </Button>
      </div>
    )
  }


}


export default SetNewPassword ;
