import React from 'react';
import axios from "axios";


import {
  Container ,
  Card ,
  ListGroup ,
  Button,
  Col ,
  Badge ,
  Alert ,
  Row ,
  Grid ,

} from 'react-bootstrap';
import { connect } from "react-redux";


class Profile extends React.Component {
  state = {
    error : null , //holds the error
    loading : true , //to determine if the page is loading
    account_info : [] , //holds the account info
    selected_button : "info" , //changes whenever a user selected a button. is on the top button by default

  }

  componentDidMount(){
    this.handleGetAccount() ;
  }

  handleGetAccount = () => {

  }

  handleProfileInfo = () => {
    switch(this.state.selected_button){
      case "info" :
        return(
          <h5>
            account info
          </h5>
        )
        break ;
      default :
        return(
          <h5>
          default case
          </h5>
        )
    }
  }

  render(){
    const {
      error ,
      loading ,
      account_info ,
      selected_button ,

    } = this.state

    const {
      username ,
      authenticated ,

    } = this.props

    console.log(selected_button)
    return(
      <React.Fragment>
        {
          authenticated  ?
            <Container>
              <h4>Account info for {username}</h4>
              <Row>
                <Col sm={{ span: 3 }}>
                  <ListGroup variant="flush">
                    <ListGroup.Item>Account Info</ListGroup.Item>
                    <ListGroup.Item>Email Preferences</ListGroup.Item>
                  </ListGroup>
                </Col>
                <Col sm={{ span: 6}} >
                  {this.handleProfileInfo()}
                </Col>
              </Row>
            </Container>
          :
            <h5>how'd you get here??</h5>
        }
      </React.Fragment>
    )
  }

}



const mapStateToProps = (state) => {
  return {
    username : state.auth.username ,
    token : state.auth.token ,
    authenticated: state.auth.token !== null ,

  }
}

export default
  connect(
    mapStateToProps ,

  )(Profile)
