  }

  handleProfileInfo = () => {

  }

  render(){
    const {
      error ,
      loading ,
      account_info ,
      selected_button ,

    } = this.state

    const {
      username ,
      authenticated ,

    } = this.props

    console.log(selected_button)
    return(
      <React.Fragment>
        {
          authenticated  ?
            <Container>
              <h4>Account info for {username}</h4>

              <Col sm={{ span: 3 }}>
                <ListGroup variant="flush">
                  <ListGroup.Item>Account Info</ListGroup.Item>
                  <ListGroup.Item>Email Preferences</ListGroup.Item>
                </ListGroup>
              </Col>
              <Col sm={{ span: 6}} >
                {
                  selected_button === "info" ?
                    <h3>
                      account info
                    </h3>
                  :

                    <h3>
                      default case
                    </h3>

                }
              </Col>
            </Container>
          :
            <h5>how'd you get here??</h5>
        }
      </React.Fragment>
    )
  }

}



const mapStateToProps = (state) => {
  return {
    username : state.auth.username ,
    token : state.auth.token ,
    authenticated: state.auth.token !== null ,

  }
}

export default
  connect(
    mapStateToProps ,

  )(Profile)
