import React from "react" ;
import {withRouter} from "react-router";
import {Link} from "react-router-dom";
import axios from "axios";
// import RichText from 'prismic-reactjs';
import {
  Container ,
  Spinner ,
  Card ,
  ListGroup ,
  Button,
  Form ,
  Col ,
  Popover ,
  OverlayTrigger ,
  Badge ,
  Alert ,
  Row ,

} from 'react-bootstrap';
import { connect } from "react-redux";

import RichText from './Misc/RichText';
import LoaderSpinner from './Misc/LoaderSpinner'
import {
  all_articles_url,

} from "../backend-urls.js" ;

import "../content/css/App.css";




class Home extends React.Component {

  state = {
    error: null , //stores the error that comes from the backend
    loading : false , //to determine if the page is loading
    articles : [] , //stores all of the articles retrieved from the database

  }

  componentDidMount(){
    this.setState({
      loading:true ,
    })
    this.handleGetArticles();
  }


  handleGetArticles = () => {
    axios
      .get(all_articles_url)
      .then(response => {

        this.setState({
          loading: false ,
          articles: response.data ,
        })
      })
      .catch(error => {
        this.setState({
          error: error ,
          loading : false
        })
      })
  }






  render(){

    const {
      loading ,
      error ,
      articles ,
    } = this.state

    const {
      authenticated ,
    } = this.props


    //for email popover
    const email_popover = (
      <Popover id="popover-basic">
        <Popover.Title as="h3">Email Updates</Popover.Title>
        <Popover.Content>
          <Form >
            <Form.Row>
              <Col>
                <Form.Control type="email" placeholder="Enter email" />
              </Col>
              <Col>
                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </Col>
            </Form.Row>
          </Form>
        </Popover.Content>
      </Popover>
    )

    return(

      <React.Fragment>
        <Container>
          <Row>
            <Col sm={{span:4}}>
              <h1 className="times-new-roman-font">
                The Apollo Blog
              </h1>
            </Col>
            <Col sm={{offset:9}}>
              {
                authenticated ?
                  <div style={{float:"right" , position:"absolute"}}>
                    <Link to="/create-article" >
                      <Button variant="outline-secondary">Write an Article</Button>
                    </Link>
                  </div>
                :
                  <OverlayTrigger trigger="click" placement="left" overlay={email_popover}>
                    <Button variant="outline-primary">Sign up for email updates</Button>
                  </OverlayTrigger>
              }


            </Col>
          </Row>


        </Container>
        <hr />
        <br />

        <ListGroup variant="flush">
          {
            articles.length > 0 ?
            articles.map(article =>  (
                <ListGroup.Item>
                  <Link to={'blog/article/'+article.id} className="article-link">
                    <Card className="width-100-percent" id={article.id}>
                      <Card.Body>
                        <Card.Title>{article.title}</Card.Title>
                        <Card.Subtitle className="mb-2 text-muted">By <a href='#'>{article.author}</a> <Badge>{  article.created_date } </Badge></Card.Subtitle>
                        <Card.Text >
                          <RichText text={article.body.substring(0, 150) + '...'} />
                        </Card.Text>
                      </Card.Body>
                    </Card>
                  </Link>
                </ListGroup.Item>
              )
            )
            :
              <Alert variant="dark">
                There was an error retrieving our articles
              </Alert>
          }

        </ListGroup>


      </ React.Fragment>


    )
  }
}


const mapStateToProps = (state) => {
  return {
    authenticated: state.auth.token !== null ,
  }
}


export default
  connect(
    mapStateToProps,

  )(Home) ;
