            customAdd: function (editor) {
                increaseListIndention(editor, {
                    allListTypes: def.allListTypes,
                    listItemType: def.listItemType,
                });
            },
            customRemove: function (editor) {
                decreaseListIndention(editor, {
                    allListTypes: def.allListTypes,
