import React from "react";
import {Link} from "react-router-dom";
import {
  Container ,
  Badge,
  Col,
  Row,
  Form,
  ListGroup,
  Button,
  OverlayTrigger ,
  Popover,
  Card,

} from 'react-bootstrap';
import "../content/css/App.css";

import {
  create_article_url ,

} from "../backend-urls.js" ;

//for react-medium-editor. found at https://github.com/wangzuo/react-medium-editor
import Editor from 'react-medium-editor';
require('medium-editor/dist/css/medium-editor.css');
require('medium-editor/dist/css/themes/default.css');


class CreateArticle extends React.Component  {

  state = {
    loading : false ,
    author : null ,
    title : null ,
    body : null,

  }

  componentDidMount(){
    this.setState({
      loading : false
    })
  }

  handleAuthor = (e , data) => {
    this.setState({
      author : e.target.value
    })
  }

  handleTitle = () => {

  }

  handleBody = () => {

  }


  render(){
    const {
      author ,
      title ,
      body ,
    } = this.state ;


    return(

      <Container>
        <Row>
            <Col xs={2}>
            </Col>
            <Col xs={8} >

              <Card >
                <Card.Header><h3>Add an Article</h3></Card.Header>
                <Card.Body>
                  <Card.Text>
                    <Form>
                      <Form.Row>
                        <Form.Label>Your Name:</Form.Label>
                        <Form.Control
                          onChange = {this.handleAuthor()}
                          type="text"
                          placeholder="Enter Name"
                          />
                          {console.log(author)}
                      </Form.Row>
                      <br />
                      <Form.Row>
                        <Form.Label>Article Title:</Form.Label>
                        <Form.Control type="text" placeholder="Enter Name" />
                      </Form.Row>
                      <br/>
                      <Form.Row>
                        <Col>
                          <Form.Label>Article Body:</Form.Label>
                          <Editor
                            className="editor_form_control"
                            text={this.state.text}
                            onChange={this.handleChange}
                            options={{
                                placeholder: {
                                  text: 'Start Writing...',
                                  hideOnClick: false
                                } ,
                              }}
                            />
                            <small>Hint: highlight text that you want to change</small>
                        </Col>
                      </Form.Row>
                    </Form>
                  </Card.Text>
                  <Button variant="primary">Add Comment</Button>
                </Card.Body>
              </Card>
            </Col>
        </Row>
      </Container>
    )
  }

}

  export default CreateArticle;
