import React from "react";
import {
  Button ,
  FormGroup ,
  FormControl ,
  Badge ,
  Alert ,

} from "react-bootstrap";
import { connect } from "react-redux";
import { NavLink, Redirect } from "react-router-dom";
import {withRouter} from 'react-router';


import "../../content/css/App.css";
import { authLogin } from "../../store/actions/auth";




class Login extends React.Component {
  state = {
    username: "" , //stores the username that is getting entered
    password: "" , //stores the password that is getting entered

  }

  handleUsername = e => {
    this.setState({ username: e.target.value });
  };

  handlePassword = e => {
    this.setState({ password: e.target.value });
  };

  //
  validateForm = () =>  {
    console.log(this.state.email.length)
    console.log(this.state.email.length > 0 && this.state.password.length > 8)
    return this.state.email.length > 0 && this.state.password.length > 8;
  }

  handleSubmit = (event) => {

    event.preventDefault();
    const {username, password} = this.state;
    this.props.login(username, password)
  }

  handleValidated = () => {
    return (this.state.username.length > 1 && this.state.password.length > 1 )
  }

  render(){
    const { error, loading, token } = this.props;
    const { username, password } = this.state;

    //redirects the user to the home page if they are logged in
    if(token){
      return <Redirect to='/' />
    }

    error &&
    console.log(error.non_field_errors )
    return (


      <div className="account-form">

        <form onSubmit={this.handleSubmit}>
          <h3>Login </h3>
          <hr />
          <FormGroup controlId="email" bsSize="large">
            Username
            <FormControl
              required
              autoFocus
              value={username}
              onChange= {this.handleUsername}
            />
          </FormGroup>
          <FormGroup controlId="password" bsSize="large">
            Password
            <FormControl
              required
              value={password}
              onChange= {this.handlePassword}
              type="password"
            />
          </FormGroup>
          <Button
            block
            bsSize="large"
            disabled={
              !(this.handleValidated())
            }
            type="submit"
          >
            Login
          </Button>
          <Badge> New to Apollo? <NavLink to="/signup">Sign Up</NavLink> </Badge>
          {
            error &&

              <Alert variant="danger">{error.message.body}</Alert>

          }
        </form>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    token: state.auth.token,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    login: (username, password) => dispatch(authLogin(username, password)),
    // mergeCartOnLogin: () => dispatch(mergeCartOnLogin()),

  };
};

export default withRouter(
    connect(
      mapStateToProps,
      mapDispatchToProps
    )(Login)
  );
