import React from "react";
import {Link} from "react-router-dom";
import axios from 'axios';
import {
  Container ,
  Badge ,
  Col ,
  Row ,
  Form ,
  ListGroup,
  Button ,
  OverlayTrigger ,
  Popover ,
  Card ,
  Alert ,

} from 'react-bootstrap';
import { connect } from "react-redux";


import "../content/css/App.css";

import {
  create_article_url ,

} from "../backend-urls.js" ;

//for ReactQuill. found at https://github.com/zenoamaro/react-quill
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';



class CreateArticle extends React.Component  {

  state = {
    error: null , //stores the error that comes from the backend
    loading : false , //to determine if the page is loading
    author : null , //stores the author (from the author input)
    title : null , //stores the title (from the title input)
    body : null , //stores the body (from the body input)
    article_submitted : false , //to determine if the article was successfully submitted (if true, display a banner at the bottom of the page and redirect the user)
    countdown: 3 , //countdown to redirect, after the article gets submitted

  }



  componentDidMount(){
    this.setState({
      loading : false
    })
  }

  handleAuthor = (text) => {
        console.log(text.target.value)
    this.setState({
      author: text.target.value
    });
  }

  handleTitle = (text) => {
    console.log(text)
    this.setState({
      title: text.target.value
    });
  }

  handleBody = (text) => {
    console.log(text)
    this.setState({
      body: text
    });
  }

  submitForm = () => {

    // creates a new FormData object and adds all the form data to it
    var data = new FormData();
    data.append('author' , this.state.author) ;
    data.append('title' , this.state.title) ;
    data.append('body' , this.state.body) ;

    axios
      .post(create_article_url , data)
      .then(response => {
        this.setState({
          article_submitted : true
        })
        console.log(response.data);
      })
      .catch(error => {
        console.log(error)
        this.setState({
          error: error ,
          loading : false
        })
      }) ;
  }

  countdown = ( s ) => {
      s -= 1 ;
      this.setState({
        countdown : s
      }) ;
  }




  render(){
    const {
      author ,
      title ,
      body ,
      article_submitted ,
      countdown ,

    } = this.state ;

    //for counting down the "redirect" (redirect in 3.. 2.. 1..)
    // article_submitted &&
    //   setInterval(function(){
    //     s = 5 ;
    //     this.setState({
    //
    //     })
    //   }  , 1000 );


    return(

      <Container>
        <Row>
            <Col xs={2}>
            </Col>
            <Col xs={8} >

              <Card >
                <Card.Header><h3>Add an Article</h3></Card.Header>
                <Card.Body>
                  <Card.Text>
                    <Form>
                      <Form.Row>
                        <Form.Label>Your Name:</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Enter Name"
                          onChange = {this.handleAuthor}
                        />
                      </Form.Row>
                      <br />
                      <Form.Row>
                        <Form.Label>Article Title:</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="Enter Name"
                          onChange = {this.handleTitle}
                        />
                      </Form.Row>
                      <br/>
                      <Form.Row>
                        <Col>
                          <Form.Label>Article Body:</Form.Label>
                          <ReactQuill
                            placeholder="Start Writing..."
                            onChange={this.handleBody}
                          />

                        </Col>
                      </Form.Row>
                    </Form>
                  </Card.Text>
                  <Button
                    variant="primary"
                    onClick={this.submitForm}
                  >
                    Add Article
                  </Button>
                </Card.Body>
              </Card>
              {
                article_submitted  &&
                  <React.Fragment>
                    <br/>
                    <meta http-equiv="refresh" content="3;url=/" />
                    <Alert variant="success">
                      You successfully created an article! You will be redirected in {countdown} seconds
                    </Alert>
                  </React.Fragment>
              }


            </Col>
        </Row>
      </Container>
    )
  }

}

const mapStateToProps = state => {
  console.log(state.auth.token)
  return {
    loading: state.auth.loading ,
    error: state.auth.error ,
    token: state.auth.token ,
    authenticated: state.auth.token !== null ,
  };
};

export default
  connect(
    mapStateToProps,

  )(CreateArticle);
