import LoaderSpinner from './Misc/LoaderSpinner';
import "../content/css/App.css";
import {
  article_url,
  create_email_url ,

} from "../backend-urls.js" ;




class ArticlePage extends React.Component  {

  state = {
    loading : false , //to determine if the page is loading
    error : null , //stores the error that comes from backend
    article_data: [] , //stores the article information
    commenter_name: "" , //stores the name of the person commenting
    comment_body : "" , //stores the body of the comment

  }

  componentDidMount(){
    this.setState({
      loading: true ,
    })

    this.handleGetArticle() ;

  }

  handleGetArticle = () => {
    var article_id = this.props.match.params.article_id
    axios
      .get(article_url(article_id) )
      .then(response => {
        //console.log(response.data);
        this.setState({
          loading: false ,
          article_data: response.data ,
        })
      })
      .catch(error => {
        this.setState({
          error: error ,
          loading : false
        })
      })
  }


  handleCommentBody = (text) => {
    this.setState({
      comment_body : text.target.value
    })
  }

  handleCommenterName = (text) => {
    this.setState({
      commenter_name : text.target.value
    })
  }

