                                <Form.Label>Name:</Form.Label>
                                <Form.Control
                                  type="text"
                                  placeholder="Enter Name"
                                  onChange={this.handleCommenterName}
                                />
                              </Form.Row>
                              <br/>
                              <Form.Row>
                                <Col>
                                  <Form.Label>Comment:</Form.Label>

                                    <ReactQuill
                                      placeholder="Start Writing..."
                                      onChange={this.handleCommentBody}
                                    />
                                </Col>
                              </Form.Row>
                            </Form>
                          </Card.Text>
                          <Button
